mapper.select:
	 select * from (
    select rownum r_num, a.* from (
     select * from sinfo order by inum desc
    ) a
 ) where r_num BETWEEN #{begin} and #{end}

	 select * from (
    select rownum r_num, a.* from (
     select * from sinfo order by inum desc
    ) a
 ) where snum = #{snum} and r_num BETWEEN #{begin} and #{end}
